ut=\"light\" admin_label=\"Button\" custom_button=\"on\" button_text_size=\"20\" button_text_color=\"#191919\" button_use_icon=\"on\" button_icon_placement=\"right\" button_on_hover=\"on\" button_letter_spacing_hover=\"0\" custom_css_main_element=\"margin-left:25px;||bottom:25px;||width:170px;\" disabled=\"off\"] [/et_pb_button][/et_pb_column][et_pb_column type=\"1_2\" disabled=\"off\" parallax=\"off\" parallax_method=\"on\"][et_pb_image src=\"http://april.dev/wp-content/uploads/2017/01/Inboundcon-backstage-3.jpg\" show_in_lightbox=\"off\" url_new_window=\"off\" use_overlay=\"off\" sticky=\"off\" align=\"left\" force_fullwidth=\"off\" always_center_on_mobile=\"on\" admin_label=\"Image\" use_border_color=\"off\" border_color=\"#ffffff\" border_width=\"1px\" border_style=\"solid\" disabled=\"off\"] [/et_pb_image][/et_pb_column][/et_pb_row][et_pb_row make_fullwidth=\"off\" use_custom_width=\"off\" width_unit=\"off\" custom_width_px=\"1080px\" custom_width_percent=\"80%\" use_custom_gutter=\"off\" gutter_width=\"3\" custom_margin=\"|||\" background_color=\"#ffffff\" allow_player_pause=\"off\" parallax=\"off\" parallax_method=\"on\" make_equal=\"off\" parallax_1=\"off\" parallax_method_1=\"on\" parallax_2=\"off\" parallax_method_2=\"on\" parallax_3=\"off\" parallax_method_3=\"on\" parallax_4=\"off\" parallax_method_4=\"on\" admin_label=\"row\" disabled=\"off\"][et_pb_column type=\"4_4\" disabled=\"off\" parallax=\"off\" parallax_method=\"on\"][et_pb_text background_layout=\"light\" text_orientation=\"left\" admin_label=\"Text\" use_border_color=\"off\" border_style=\"solid\" custom_padding=\"25px|25px|25px|25px\" disabled=\"off\"]<p class=\"p1\"><span style=\"font-size: medium; color: #232323;\"><span class=\"s1\"><b>Private half-day framing workshop</b></span><span class=\"s2\"><b> ($2K + travel)</b></span></span></p>\n<p class=\"p3\"><span style=\"font-size: medium; color: #232323;\"><span class=\"s1\">I come to your office and spend a half day working with your team.</span>The schedule for the day looks like this.</span></p>\n<h4 class=\"p1\" style=\"text-align: left;\"></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s1\"><b>9:009:20</b></span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s2\">Introduction to product framing and positioning including why we stink at it</span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s1\"><b>9:20-10:00    </b></span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s2\">A walkthrough of the framing process including the inputs and outputs</span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s1\"><b>10:0010:30   </b></span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s2\">De-framing: a discussion of the current product frame and</span><span class=\"s2\">how to de-frame that product</span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s1\"><b>10:30- 11:00 </b>  </span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s2\">An interactive discussion working through the long list of the</span><span class=\"s2\">companys unique attributes</span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s1\"><b>11:00  11:30</b>   </span></span></h4>\n<h4 class=\"p1\" style=\"text-align: left;\"><span style=\"font-size: medium;\"><span class=\"s2\">Value  we then work through the value that is mapped to the</span>unique attributes</span></h4>\n<h4><span style=\"font-size: medium; color: #353535;\"><strong>11:30 - 12:00</strong>   </span></h4>\n<h4><span style=\"font-size: medium; col